/**
 * ContextMenu Component
 *
 * 设计决策:
 * - 通过 onContextMenu 事件触发
 * - 位置跟随鼠标右键点击位置
 * - 自动处理边界检测（防止超出屏幕）
 * - 支持嵌套子菜单
 * - ESC 或点击外部关闭
 */

import React, { useState, useEffect, useRef, createContext, useContext, useCallback } from 'react';
import { cn } from '@/lib/utils';
import { ChevronRight, Check } from 'lucide-react';

// ============================================================
// Types
// ============================================================

interface ContextMenuItemData {
  label: string;
  shortcut?: string;
  icon?: React.ReactNode;
  disabled?: boolean;
  checked?: boolean;
  danger?: boolean;
  onClick?: () => void;
  submenu?: ContextMenuItemData[];
  separator?: boolean;
}

interface ContextMenuState {
  isOpen: boolean;
  x: number;
  y: number;
}

// ============================================================
// Context
// ============================================================

interface ContextMenuContextValue {
  close: () => void;
}

const ContextMenuContext = createContext<ContextMenuContextValue | null>(null);

// ============================================================
// Hook: useContextMenu
// ============================================================

export function useContextMenu() {
  const [state, setState] = useState<ContextMenuState>({
    isOpen: false,
    x: 0,
    y: 0,
  });

  const open = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    // 计算位置，考虑边界
    let x = e.clientX;
    let y = e.clientY;

    // 简单的边界检测（假设菜单最大 250px 宽，300px 高）
    const menuWidth = 250;
    const menuHeight = 300;

    if (x + menuWidth > window.innerWidth) {
      x = window.innerWidth - menuWidth - 10;
    }
    if (y + menuHeight > window.innerHeight) {
      y = window.innerHeight - menuHeight - 10;
    }

    setState({ isOpen: true, x, y });
  }, []);

  const close = useCallback(() => {
    setState(prev => ({ ...prev, isOpen: false }));
  }, []);

  return { state, open, close };
}

// ============================================================
// ContextMenu Component
// ============================================================

interface ContextMenuProps {
  state: ContextMenuState;
  items: ContextMenuItemData[];
  onClose: () => void;
}

export const ContextMenu: React.FC<ContextMenuProps> = ({ state, items, onClose }) => {
  const menuRef = useRef<HTMLDivElement>(null);

  // 点击外部关闭
  useEffect(() => {
    if (!state.isOpen) return;

    const handleClickOutside = (e: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(e.target as Node)) {
        onClose();
      }
    };

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    // 延迟添加监听器，防止触发右键的点击立即关闭菜单
    const timer = setTimeout(() => {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscape);
      document.addEventListener('contextmenu', handleClickOutside);
    }, 0);

    return () => {
      clearTimeout(timer);
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscape);
      document.removeEventListener('contextmenu', handleClickOutside);
    };
  }, [state.isOpen, onClose]);

  if (!state.isOpen) return null;

  return (
    <ContextMenuContext.Provider value={{ close: onClose }}>
      <div
        ref={menuRef}
        className={cn(
          "fixed z-[100]",
          "min-w-[200px] py-1",
          "bg-[#252526] border border-[#454545] rounded-md shadow-2xl"
        )}
        style={{ left: state.x, top: state.y }}
      >
        {items.map((item, index) => (
          item.separator ? (
            <ContextMenuSeparator key={`sep-${index}`} />
          ) : (
            <ContextMenuItem key={`${item.label}-${index}`} item={item} />
          )
        ))}
      </div>
    </ContextMenuContext.Provider>
  );
};

// ============================================================
// ContextMenuItem
// ============================================================

const ContextMenuItem: React.FC<{ item: ContextMenuItemData }> = ({ item }) => {
  const context = useContext(ContextMenuContext);
  const [showSubmenu, setShowSubmenu] = useState(false);
  const hasSubmenu = item.submenu && item.submenu.length > 0;

  const handleClick = () => {
    if (item.disabled) return;
    if (hasSubmenu) return;

    item.onClick?.();
    context?.close();
  };

  return (
    <div
      className="relative"
      onMouseEnter={() => hasSubmenu && setShowSubmenu(true)}
      onMouseLeave={() => setShowSubmenu(false)}
    >
      <div
        className={cn(
          "flex items-center px-3 py-1.5 mx-1 rounded cursor-pointer",
          item.disabled
            ? "text-[#6e6e6e] cursor-not-allowed"
            : item.danger
              ? "text-red-400 hover:bg-red-500/20"
              : "text-[#cccccc] hover:bg-[#094771]",
          showSubmenu && hasSubmenu && "bg-[#094771]"
        )}
        onClick={handleClick}
      >
        {/* Icon */}
        <span className="w-5 flex-shrink-0">
          {item.checked !== undefined ? (
            item.checked ? <Check size={14} /> : null
          ) : item.icon ? (
            <span className="text-[#858585]">{item.icon}</span>
          ) : null}
        </span>

        {/* Label */}
        <span className="flex-1 text-xs">{item.label}</span>

        {/* Shortcut or Arrow */}
        {hasSubmenu ? (
          <ChevronRight size={14} className="text-[#858585] ml-4" />
        ) : item.shortcut ? (
          <span className="text-[#858585] text-xs ml-4">{item.shortcut}</span>
        ) : null}
      </div>

      {/* Submenu */}
      {hasSubmenu && showSubmenu && (
        <div
          className={cn(
            "absolute left-full top-0 ml-0.5",
            "min-w-[180px] py-1",
            "bg-[#252526] border border-[#454545] rounded-md shadow-xl"
          )}
        >
          {item.submenu!.map((subItem, subIndex) => (
            subItem.separator ? (
              <ContextMenuSeparator key={`sub-sep-${subIndex}`} />
            ) : (
              <ContextMenuSubItem key={subItem.label} item={subItem} />
            )
          ))}
        </div>
      )}
    </div>
  );
};

// ============================================================
// ContextMenuSubItem
// ============================================================

const ContextMenuSubItem: React.FC<{ item: ContextMenuItemData }> = ({ item }) => {
  const context = useContext(ContextMenuContext);

  const handleClick = () => {
    if (item.disabled) return;
    item.onClick?.();
    context?.close();
  };

  return (
    <div
      className={cn(
        "flex items-center px-3 py-1.5 mx-1 rounded cursor-pointer",
        item.disabled
          ? "text-[#6e6e6e] cursor-not-allowed"
          : item.danger
            ? "text-red-400 hover:bg-red-500/20"
            : "text-[#cccccc] hover:bg-[#094771]"
      )}
      onClick={handleClick}
    >
      <span className="w-5 flex-shrink-0">
        {item.checked && <Check size={14} />}
        {item.icon && !item.checked && <span className="text-[#858585]">{item.icon}</span>}
      </span>
      <span className="flex-1 text-xs">{item.label}</span>
      {item.shortcut && (
        <span className="text-[#858585] text-xs ml-4">{item.shortcut}</span>
      )}
    </div>
  );
};

// ============================================================
// ContextMenuSeparator
// ============================================================

const ContextMenuSeparator: React.FC = () => (
  <div className="h-px bg-[#454545] my-1 mx-2" />
);

export type { ContextMenuItemData };

export { Menu, MenuTrigger, MenuContent, MenuBar } from './Menu';
export type { MenuItemData } from './Menu';

export { ContextMenu, useContextMenu } from './ContextMenu';
export type { ContextMenuItemData } from './ContextMenu';

export { Tooltip } from './Tooltip';

export { ResizeHandle, useResizable } from './ResizeHandle';

export { MarkdownPreview } from './MarkdownPreview';

/**
 * MarkdownPreview - Markdown 渲染组件
 *
 * 使用 react-markdown 渲染 Markdown 内容
 * 支持 GitHub Flavored Markdown (GFM)
 */

import React from 'react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { cn } from '@/lib/utils';

interface MarkdownPreviewProps {
  content: string;
  className?: string;
}

export const MarkdownPreview: React.FC<MarkdownPreviewProps> = ({ content, className }) => {
  return (
    <div className={cn("markdown-preview p-4 overflow-auto", className)}>
      <ReactMarkdown
        remarkPlugins={[remarkGfm]}
        components={{
          // 标题
          h1: ({ children }) => (
            <h1 className="text-2xl font-bold text-white mb-4 pb-2 border-b border-[#404040]">
              {children}
            </h1>
          ),
          h2: ({ children }) => (
            <h2 className="text-xl font-bold text-white mb-3 mt-6 pb-1 border-b border-[#404040]">
              {children}
            </h2>
          ),
          h3: ({ children }) => (
            <h3 className="text-lg font-semibold text-white mb-2 mt-4">
              {children}
            </h3>
          ),
          h4: ({ children }) => (
            <h4 className="text-base font-semibold text-white mb-2 mt-3">
              {children}
            </h4>
          ),

          // 段落
          p: ({ children }) => (
            <p className="text-[#cccccc] mb-3 leading-relaxed">
              {children}
            </p>
          ),

          // 列表
          ul: ({ children }) => (
            <ul className="list-disc list-inside text-[#cccccc] mb-3 space-y-1 ml-2">
              {children}
            </ul>
          ),
          ol: ({ children }) => (
            <ol className="list-decimal list-inside text-[#cccccc] mb-3 space-y-1 ml-2">
              {children}
            </ol>
          ),
          li: ({ children }) => (
            <li className="text-[#cccccc]">{children}</li>
          ),

          // 代码
          code: ({ className, children, ...props }) => {
            const match = /language-(\w+)/.exec(className || '');
            const isInline = !match;

            if (isInline) {
              return (
                <code className="px-1.5 py-0.5 bg-[#3c3c3c] rounded text-[#e06c75] text-sm font-mono" {...props}>
                  {children}
                </code>
              );
            }

            return (
              <code className={cn("block", className)} {...props}>
                {children}
              </code>
            );
          },
          pre: ({ children }) => (
            <pre className="bg-[#2d2d30] rounded-lg p-4 mb-3 overflow-x-auto border border-[#404040]">
              <code className="text-sm font-mono text-[#d4d4d4]">
                {children}
              </code>
            </pre>
          ),

          // 引用
          blockquote: ({ children }) => (
            <blockquote className="border-l-4 border-[#007acc] pl-4 my-3 text-[#858585] italic">
              {children}
            </blockquote>
          ),

          // 链接
          a: ({ href, children }) => (
            <a
              href={href}
              className="text-[#58a6ff] hover:underline"
              target="_blank"
              rel="noopener noreferrer"
            >
              {children}
            </a>
          ),

          // 图片
          img: ({ src, alt }) => (
            <img
              src={src}
              alt={alt}
              className="max-w-full h-auto rounded my-3"
            />
          ),

          // 表格
          table: ({ children }) => (
            <div className="overflow-x-auto mb-3">
              <table className="min-w-full border-collapse border border-[#404040]">
                {children}
              </table>
            </div>
          ),
          thead: ({ children }) => (
            <thead className="bg-[#2d2d30]">{children}</thead>
          ),
          th: ({ children }) => (
            <th className="border border-[#404040] px-3 py-2 text-left text-white font-semibold">
              {children}
            </th>
          ),
          td: ({ children }) => (
            <td className="border border-[#404040] px-3 py-2 text-[#cccccc]">
              {children}
            </td>
          ),

          // 水平线
          hr: () => (
            <hr className="my-4 border-[#404040]" />
          ),

          // 强调
          strong: ({ children }) => (
            <strong className="font-bold text-white">{children}</strong>
          ),
          em: ({ children }) => (
            <em className="italic text-[#cccccc]">{children}</em>
          ),

          // 删除线
          del: ({ children }) => (
            <del className="text-[#858585] line-through">{children}</del>
          ),

          // 任务列表
          input: ({ checked }) => (
            <input
              type="checkbox"
              checked={checked}
              readOnly
              className="mr-2 accent-[#007acc]"
            />
          ),
        }}
      >
        {content}
      </ReactMarkdown>
    </div>
  );
};

export default MarkdownPreview;

/**
 * Menu Component System
 *
 * 设计决策:
 * - 使用 React Context 管理菜单状态
 * - 支持无限嵌套子菜单
 * - 支持键盘导航 (Arrow keys, Enter, Escape)
 * - 点击外部自动关闭
 * - 支持分隔线和禁用状态
 */

import React, { createContext, useContext, useState, useRef, useEffect, useCallback } from 'react';
import { cn } from '@/lib/utils';
import { ChevronRight, Check } from 'lucide-react';

// ============================================================
// Types
// ============================================================

interface MenuContextValue {
  isOpen: boolean;
  setIsOpen: (open: boolean) => void;
  activeSubmenu: string | null;
  setActiveSubmenu: (id: string | null) => void;
}

interface MenuItemData {
  label: string;
  shortcut?: string;
  icon?: React.ReactNode;
  disabled?: boolean;
  checked?: boolean;
  onClick?: () => void;
  submenu?: MenuItemData[];
  separator?: boolean;
}

// ============================================================
// Context
// ============================================================

const MenuContext = createContext<MenuContextValue | null>(null);

const useMenu = () => {
  const context = useContext(MenuContext);
  if (!context) throw new Error('useMenu must be used within Menu');
  return context;
};

// ============================================================
// Menu Root
// ============================================================

interface MenuProps {
  children: React.ReactNode;
}

export const Menu: React.FC<MenuProps> = ({ children }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSubmenu, setActiveSubmenu] = useState<string | null>(null);

  return (
    <MenuContext.Provider value={{ isOpen, setIsOpen, activeSubmenu, setActiveSubmenu }}>
      <div className="relative inline-block">
        {children}
      </div>
    </MenuContext.Provider>
  );
};

// ============================================================
// Menu Trigger
// ============================================================

interface MenuTriggerProps {
  children: React.ReactNode;
  className?: string;
}

export const MenuTrigger: React.FC<MenuTriggerProps> = ({ children, className }) => {
  const { isOpen, setIsOpen } = useMenu();

  return (
    <button
      className={cn(
        "px-2 py-1 text-xs rounded transition-colors",
        isOpen ? "bg-[#094771] text-white" : "hover:bg-[#3c3c3c] text-[#cccccc]",
        className
      )}
      onClick={() => setIsOpen(!isOpen)}
      onMouseEnter={() => {
        // 如果已经有菜单打开，hover 时也打开这个
        const parentMenu = document.querySelector('[data-menu-open="true"]');
        if (parentMenu) setIsOpen(true);
      }}
    >
      {children}
    </button>
  );
};

// ============================================================
// Menu Content
// ============================================================

interface MenuContentProps {
  items: MenuItemData[];
  className?: string;
}

export const MenuContent: React.FC<MenuContentProps> = ({ items, className }) => {
  const { isOpen, setIsOpen, setActiveSubmenu } = useMenu();
  const menuRef = useRef<HTMLDivElement>(null);

  // 点击外部关闭
  useEffect(() => {
    if (!isOpen) return;

    const handleClickOutside = (e: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(e.target as Node)) {
        setIsOpen(false);
        setActiveSubmenu(null);
      }
    };

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        setIsOpen(false);
        setActiveSubmenu(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('keydown', handleEscape);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, setIsOpen, setActiveSubmenu]);

  if (!isOpen) return null;

  return (
    <div
      ref={menuRef}
      data-menu-open={isOpen}
      className={cn(
        "absolute top-full left-0 mt-0.5 z-50",
        "min-w-[200px] py-1",
        "bg-[#252526] border border-[#454545] rounded-md shadow-xl",
        className
      )}
    >
      {items.map((item, index) => (
        item.separator ? (
          <MenuSeparator key={`sep-${index}`} />
        ) : (
          <MenuItem key={item.label} item={item} index={index} />
        )
      ))}
    </div>
  );
};

// ============================================================
// Menu Item
// ============================================================

interface MenuItemProps {
  item: MenuItemData;
  index: number;
}

const MenuItem: React.FC<MenuItemProps> = ({ item, index }) => {
  const { setIsOpen, activeSubmenu, setActiveSubmenu } = useMenu();
  const [showSubmenu, setShowSubmenu] = useState(false);
  const itemRef = useRef<HTMLDivElement>(null);
  const submenuId = `submenu-${index}`;

  const hasSubmenu = item.submenu && item.submenu.length > 0;

  const handleClick = () => {
    if (item.disabled) return;
    if (hasSubmenu) return; // 子菜单不触发点击

    item.onClick?.();
    setIsOpen(false);
    setActiveSubmenu(null);
  };

  const handleMouseEnter = () => {
    if (hasSubmenu) {
      setActiveSubmenu(submenuId);
      setShowSubmenu(true);
    } else {
      setActiveSubmenu(null);
    }
  };

  const handleMouseLeave = () => {
    // 延迟关闭，给用户时间移动到子菜单
    setTimeout(() => {
      if (activeSubmenu !== submenuId) {
        setShowSubmenu(false);
      }
    }, 100);
  };

  return (
    <div
      ref={itemRef}
      className="relative"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div
        className={cn(
          "flex items-center px-3 py-1.5 mx-1 rounded cursor-pointer",
          item.disabled
            ? "text-[#6e6e6e] cursor-not-allowed"
            : "text-[#cccccc] hover:bg-[#094771]",
          (showSubmenu && hasSubmenu) && "bg-[#094771]"
        )}
        onClick={handleClick}
      >
        {/* Checkbox / Icon */}
        <span className="w-5 flex-shrink-0">
          {item.checked !== undefined ? (
            item.checked ? <Check size={14} /> : null
          ) : item.icon ? (
            item.icon
          ) : null}
        </span>

        {/* Label */}
        <span className="flex-1 text-xs">{item.label}</span>

        {/* Shortcut or Submenu Arrow */}
        {hasSubmenu ? (
          <ChevronRight size={14} className="text-[#858585] ml-4" />
        ) : item.shortcut ? (
          <span className="text-[#858585] text-xs ml-4">{item.shortcut}</span>
        ) : null}
      </div>

      {/* Submenu */}
      {hasSubmenu && showSubmenu && (
        <div
          className={cn(
            "absolute left-full top-0 ml-0.5",
            "min-w-[200px] py-1",
            "bg-[#252526] border border-[#454545] rounded-md shadow-xl"
          )}
        >
          {item.submenu!.map((subItem, subIndex) => (
            subItem.separator ? (
              <MenuSeparator key={`sub-sep-${subIndex}`} />
            ) : (
              <SubMenuItem key={subItem.label} item={subItem} />
            )
          ))}
        </div>
      )}
    </div>
  );
};

// ============================================================
// Sub Menu Item (简化版，不支持更深嵌套)
// ============================================================

const SubMenuItem: React.FC<{ item: MenuItemData }> = ({ item }) => {
  const { setIsOpen, setActiveSubmenu } = useMenu();

  const handleClick = () => {
    if (item.disabled) return;
    item.onClick?.();
    setIsOpen(false);
    setActiveSubmenu(null);
  };

  return (
    <div
      className={cn(
        "flex items-center px-3 py-1.5 mx-1 rounded cursor-pointer",
        item.disabled
          ? "text-[#6e6e6e] cursor-not-allowed"
          : "text-[#cccccc] hover:bg-[#094771]"
      )}
      onClick={handleClick}
    >
      <span className="w-5 flex-shrink-0">
        {item.checked !== undefined ? (
          item.checked ? <Check size={14} /> : null
        ) : item.icon ? (
          item.icon
        ) : null}
      </span>
      <span className="flex-1 text-xs">{item.label}</span>
      {item.shortcut && (
        <span className="text-[#858585] text-xs ml-4">{item.shortcut}</span>
      )}
    </div>
  );
};

// ============================================================
// Menu Separator
// ============================================================

const MenuSeparator: React.FC = () => (
  <div className="h-px bg-[#454545] my-1 mx-2" />
);

// ============================================================
// Menu Bar (便捷组件)
// ============================================================

interface MenuBarProps {
  menus: {
    label: string;
    items: MenuItemData[];
  }[];
  className?: string;
}

export const MenuBar: React.FC<MenuBarProps> = ({ menus, className }) => {
  const [openMenuIndex, setOpenMenuIndex] = useState<number | null>(null);
  const menuBarRef = useRef<HTMLDivElement>(null);

  // 点击外部关闭
  useEffect(() => {
    if (openMenuIndex === null) return;

    const handleClickOutside = (e: MouseEvent) => {
      if (menuBarRef.current && !menuBarRef.current.contains(e.target as Node)) {
        setOpenMenuIndex(null);
      }
    };

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        setOpenMenuIndex(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('keydown', handleEscape);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscape);
    };
  }, [openMenuIndex]);

  return (
    <div ref={menuBarRef} className={cn("flex items-center", className)}>
      {menus.map((menu, index) => (
        <div key={menu.label} className="relative">
          <button
            className={cn(
              "px-2 py-1 text-xs rounded transition-colors",
              openMenuIndex === index
                ? "bg-[#094771] text-white"
                : "hover:bg-[#3c3c3c] text-[#cccccc]"
            )}
            onClick={() => setOpenMenuIndex(openMenuIndex === index ? null : index)}
            onMouseEnter={() => {
              if (openMenuIndex !== null) setOpenMenuIndex(index);
            }}
          >
            {menu.label}
          </button>

          {openMenuIndex === index && (
            <div
              className={cn(
                "absolute top-full left-0 mt-0.5 z-50",
                "min-w-[220px] py-1",
                "bg-[#252526] border border-[#454545] rounded-md shadow-xl"
              )}
            >
              {menu.items.map((item, itemIndex) => (
                item.separator ? (
                  <MenuSeparator key={`sep-${itemIndex}`} />
                ) : (
                  <MenuBarItem
                    key={item.label}
                    item={item}
                    onClose={() => setOpenMenuIndex(null)}
                  />
                )
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

// MenuBar 专用 Item
const MenuBarItem: React.FC<{ item: MenuItemData; onClose: () => void }> = ({ item, onClose }) => {
  const [showSubmenu, setShowSubmenu] = useState(false);
  const hasSubmenu = item.submenu && item.submenu.length > 0;

  const handleClick = () => {
    if (item.disabled) return;
    if (hasSubmenu) return;
    item.onClick?.();
    onClose();
  };

  return (
    <div
      className="relative"
      onMouseEnter={() => hasSubmenu && setShowSubmenu(true)}
      onMouseLeave={() => setShowSubmenu(false)}
    >
      <div
        className={cn(
          "flex items-center px-3 py-1.5 mx-1 rounded cursor-pointer",
          item.disabled
            ? "text-[#6e6e6e] cursor-not-allowed"
            : "text-[#cccccc] hover:bg-[#094771]",
          showSubmenu && hasSubmenu && "bg-[#094771]"
        )}
        onClick={handleClick}
      >
        <span className="w-5 flex-shrink-0">
          {item.checked !== undefined && item.checked && <Check size={14} />}
          {item.icon && !item.checked && item.icon}
        </span>
        <span className="flex-1 text-xs">{item.label}</span>
        {hasSubmenu ? (
          <ChevronRight size={14} className="text-[#858585] ml-4" />
        ) : item.shortcut ? (
          <span className="text-[#858585] text-xs ml-4">{item.shortcut}</span>
        ) : null}
      </div>

      {hasSubmenu && showSubmenu && (
        <div
          className={cn(
            "absolute left-full top-0 ml-0.5",
            "min-w-[200px] py-1",
            "bg-[#252526] border border-[#454545] rounded-md shadow-xl"
          )}
        >
          {item.submenu!.map((subItem, subIndex) => (
            subItem.separator ? (
              <MenuSeparator key={`sub-sep-${subIndex}`} />
            ) : (
              <div
                key={subItem.label}
                className={cn(
                  "flex items-center px-3 py-1.5 mx-1 rounded cursor-pointer",
                  subItem.disabled
                    ? "text-[#6e6e6e] cursor-not-allowed"
                    : "text-[#cccccc] hover:bg-[#094771]"
                )}
                onClick={() => {
                  if (!subItem.disabled) {
                    subItem.onClick?.();
                    onClose();
                  }
                }}
              >
                <span className="w-5 flex-shrink-0">
                  {subItem.checked && <Check size={14} />}
                </span>
                <span className="flex-1 text-xs">{subItem.label}</span>
                {subItem.shortcut && (
                  <span className="text-[#858585] text-xs ml-4">{subItem.shortcut}</span>
                )}
              </div>
            )
          ))}
        </div>
      )}
    </div>
  );
};

export type { MenuItemData };

/**
 * ResizeHandle - 可拖拽调整面板大小的组件
 *
 * 使用方式:
 * <ResizeHandle
 *   direction="horizontal" // 水平拖拽（调整左右宽度）
 *   onResize={(delta) => setWidth(prev => prev + delta)}
 *   minSize={200}
 *   maxSize={500}
 * />
 */

import React, { useState, useCallback, useEffect } from 'react';
import { cn } from '@/lib/utils';

interface ResizeHandleProps {
  /** 拖拽方向: horizontal=左右, vertical=上下 */
  direction: 'horizontal' | 'vertical';
  /** 拖拽时的回调，delta 为变化量 */
  onResize: (delta: number) => void;
  /** 最小尺寸 */
  minSize?: number;
  /** 最大尺寸 */
  maxSize?: number;
  /** 当前尺寸（用于边界检测） */
  currentSize?: number;
  /** 自定义类名 */
  className?: string;
  /** 是否反向（用于右侧面板） */
  reverse?: boolean;
}

export const ResizeHandle: React.FC<ResizeHandleProps> = ({
  direction,
  onResize,
  minSize = 100,
  maxSize = 800,
  currentSize,
  className,
  reverse = false,
}) => {
  const [isDragging, setIsDragging] = useState(false);
  const [startPos, setStartPos] = useState(0);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
    setStartPos(direction === 'horizontal' ? e.clientX : e.clientY);
  }, [direction]);

  useEffect(() => {
    if (!isDragging) return;

    const handleMouseMove = (e: MouseEvent) => {
      const currentPos = direction === 'horizontal' ? e.clientX : e.clientY;
      let delta = currentPos - startPos;

      // 反向时取反
      if (reverse) {
        delta = -delta;
      }

      // 边界检测
      if (currentSize !== undefined) {
        const newSize = currentSize + delta;
        if (newSize < minSize || newSize > maxSize) {
          return;
        }
      }

      onResize(delta);
      setStartPos(currentPos);
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    // 拖拽时改变光标
    document.body.style.cursor = direction === 'horizontal' ? 'col-resize' : 'row-resize';
    document.body.style.userSelect = 'none';

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
      document.body.style.cursor = '';
      document.body.style.userSelect = '';
    };
  }, [isDragging, startPos, direction, onResize, minSize, maxSize, currentSize, reverse]);

  return (
    <div
      onMouseDown={handleMouseDown}
      className={cn(
        "flex-shrink-0 transition-colors group",
        direction === 'horizontal'
          ? "w-1 cursor-col-resize hover:bg-blue-500/50"
          : "h-1 cursor-row-resize hover:bg-blue-500/50",
        isDragging && "bg-blue-500",
        className
      )}
    >
      {/* 增大点击区域 */}
      <div
        className={cn(
          "relative",
          direction === 'horizontal'
            ? "w-3 h-full -ml-1"
            : "h-3 w-full -mt-1"
        )}
      />
    </div>
  );
};

/**
 * useResizable - 管理可调整大小的 Hook
 */
export function useResizable(
  initialSize: number,
  options: {
    minSize?: number;
    maxSize?: number;
    direction?: 'horizontal' | 'vertical';
  } = {}
) {
  const { minSize = 100, maxSize = 800 } = options;
  const [size, setSize] = useState(initialSize);

  const handleResize = useCallback((delta: number) => {
    setSize(prev => {
      const newSize = prev + delta;
      return Math.min(Math.max(newSize, minSize), maxSize);
    });
  }, [minSize, maxSize]);

  const reset = useCallback(() => {
    setSize(initialSize);
  }, [initialSize]);

  return {
    size,
    setSize,
    handleResize,
    reset,
    resizeProps: {
      onResize: handleResize,
      currentSize: size,
      minSize,
      maxSize,
    },
  };
}

export default ResizeHandle;

/**
 * Tooltip Component
 *
 * 设计决策:
 * - 延迟显示（防止鼠标快速划过时闪烁）
 * - 支持多个位置（top, bottom, left, right）
 * - 自动检测边界并调整位置
 * - 支持快捷键显示
 */

import React, { useState, useRef, useEffect } from 'react';
import { cn } from '@/lib/utils';

// ============================================================
// Types
// ============================================================

type TooltipPosition = 'top' | 'bottom' | 'left' | 'right';

interface TooltipProps {
  children: React.ReactNode;
  content: React.ReactNode;
  shortcut?: string;
  position?: TooltipPosition;
  delay?: number;
  disabled?: boolean;
  className?: string;
}

// ============================================================
// Tooltip Component
// ============================================================

export const Tooltip: React.FC<TooltipProps> = ({
  children,
  content,
  shortcut,
  position = 'bottom',
  delay = 500,
  disabled = false,
  className,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [actualPosition, setActualPosition] = useState(position);
  const triggerRef = useRef<HTMLDivElement>(null);
  const tooltipRef = useRef<HTMLDivElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  const showTooltip = () => {
    if (disabled) return;
    timeoutRef.current = setTimeout(() => {
      setIsVisible(true);
    }, delay);
  };

  const hideTooltip = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
      timeoutRef.current = null;
    }
    setIsVisible(false);
  };

  // 边界检测和位置调整
  useEffect(() => {
    if (!isVisible || !tooltipRef.current || !triggerRef.current) return;

    const tooltip = tooltipRef.current;
    const trigger = triggerRef.current;
    const tooltipRect = tooltip.getBoundingClientRect();
    const triggerRect = trigger.getBoundingClientRect();

    let newPosition = position;

    // 检测是否超出边界
    if (position === 'top' && triggerRect.top - tooltipRect.height < 10) {
      newPosition = 'bottom';
    } else if (position === 'bottom' && triggerRect.bottom + tooltipRect.height > window.innerHeight - 10) {
      newPosition = 'top';
    } else if (position === 'left' && triggerRect.left - tooltipRect.width < 10) {
      newPosition = 'right';
    } else if (position === 'right' && triggerRect.right + tooltipRect.width > window.innerWidth - 10) {
      newPosition = 'left';
    }

    if (newPosition !== actualPosition) {
      setActualPosition(newPosition);
    }
  }, [isVisible, position, actualPosition]);

  // 清理
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  const positionClasses: Record<TooltipPosition, string> = {
    top: 'bottom-full left-1/2 -translate-x-1/2 mb-2',
    bottom: 'top-full left-1/2 -translate-x-1/2 mt-2',
    left: 'right-full top-1/2 -translate-y-1/2 mr-2',
    right: 'left-full top-1/2 -translate-y-1/2 ml-2',
  };

  const arrowClasses: Record<TooltipPosition, string> = {
    top: 'top-full left-1/2 -translate-x-1/2 border-t-[#3c3c3c] border-x-transparent border-b-transparent',
    bottom: 'bottom-full left-1/2 -translate-x-1/2 border-b-[#3c3c3c] border-x-transparent border-t-transparent',
    left: 'left-full top-1/2 -translate-y-1/2 border-l-[#3c3c3c] border-y-transparent border-r-transparent',
    right: 'right-full top-1/2 -translate-y-1/2 border-r-[#3c3c3c] border-y-transparent border-l-transparent',
  };

  return (
    <div
      ref={triggerRef}
      className={cn("relative inline-flex", className)}
      onMouseEnter={showTooltip}
      onMouseLeave={hideTooltip}
      onFocus={showTooltip}
      onBlur={hideTooltip}
    >
      {children}

      {isVisible && (
        <div
          ref={tooltipRef}
          className={cn(
            "absolute z-50 pointer-events-none",
            "px-2 py-1.5 rounded",
            "bg-[#3c3c3c] border border-[#454545] shadow-lg",
            "text-xs text-[#cccccc] whitespace-nowrap",
            "animate-in fade-in-0 zoom-in-95 duration-100",
            positionClasses[actualPosition]
          )}
          role="tooltip"
        >
          <div className="flex items-center gap-2">
            <span>{content}</span>
            {shortcut && (
              <span className="text-[#858585] bg-[#2d2d2d] px-1 rounded text-[10px]">
                {shortcut}
              </span>
            )}
          </div>

          {/* Arrow */}
          <div
            className={cn(
              "absolute w-0 h-0 border-4",
              arrowClasses[actualPosition]
            )}
          />
        </div>
      )}
    </div>
  );
};

export default Tooltip;
